mapper.update:
      
UPDATE TBL_HIBOARD
   SET HIBBS_ORDER = HIBBS_ORDER + 1
 WHERE HIBBS_GROUP = #{hiBbsGroup}
   AND HIBBS_ORDER >= #{hiBbsOrder}

UPDATE TBL_HIBOARD 
   SET HIBBS_READ_CNT = HIBBS_READ_CNT + 1
 WHERE HIBBS_SEQ = #{value}

mapper.select:
SELECT COUNT(HIBBS_SEQ) AS CNT
FROM TBL_HIBOARD
WHERE HIBBS_PARENT = #{value}
